import time

def solution(s):
    start = time.time()
    ret = " " # 임시로 띄어쓰기 삽입
    for c in s:
        if ret[-1] == ' ':
            ret += c.upper()
        else:
            ret += c.lower()
    end = time.time()
    print(end - start)
    return ret[1:]
